mapper.select:
select

from t_check_in

  


  order by ${example.orderByClause}


limit ${offset},${count}

mapper.insert:
insert into t_check_in (UUID, HOTEL, 
  ROOM, CHECK_IN_TIME, 
  CHECK_IN_RECEPTIONIST, CHECK_OUT_TIME, CHECK_OUT_RECEPTIONIST
  )
values (#{uuid,jdbcType=CHAR}, #{hotel,jdbcType=INTEGER}, 
  #{room,jdbcType=INTEGER}, #{checkInTime,jdbcType=TIMESTAMP}, 
  #{checkInReceptionist,jdbcType=INTEGER}, #{checkOutTime,jdbcType=TIMESTAMP}, #{checkOutReceptionist,jdbcType=INTEGER}
  )

mapper.select:
select t1.ROOM_NO, t1.HOTEL, t1.ID as ROOM, t2.ID as ID, t2.CHECK_IN_TIME, t2.CHECK_OUT_TIME, t2.CHECK_IN_RECEPTIONIST, t3.NAME as CHECK_IN_RECEPTIONIST_NAME, t2.CHECK_OUT_RECEPTIONIST, t4.NAME as CHECK_OUT_RECEPTIONIST_NAME, ifnull(t2.USE_TIMES,0) as USE_TIMES, ifnull(t2.CHARGING,0) as CHARGING, ifnull(t2.CHARGE,0) as CHARGE, ifnull(t2.CHARGE_STANDARD,0) as CHARGE_STANDARD from t_room t1 left join ( select a.*, count(c.ID) as USE_TIMES, sum(c.CHARGING) as CHARGING, sum(c.CHARGE) as CHARGE, sum(c.CHARGE_STANDARD) as CHARGE_STANDARD from t_check_in a left join (select max(s.ID) as MAX_ID from t_check_in s
and s.CHECK_IN_TIME >= #{queryForm.queryCheckInTimeFrom}
and s.CHECK_IN_TIME <= #{queryForm.queryCheckInTimeFrom}
and s.CHECK_OUT_TIME >= #{queryForm.queryCheckOutTimeFrom}
and s.CHECK_OUT_TIME <= #{queryForm.queryCheckOutTimeFrom}
group by s.ROOM) b on b.MAX_ID=a.ID left join t_device_usage c on c.ROOM=a.ROOM and c.USE_TIME >= a.CHECK_IN_TIME and c.USE_TIME <= ifnull(a.CHECK_OUT_TIME, now()) where b.MAX_ID is not null group by a.ROOM ) t2 on t2.ROOM= t1.ID left join t_user t3 on t3.ID=t2.CHECK_IN_RECEPTIONIST left join t_user t4 on t4.ID=t2.CHECK_OUT_RECEPTIONIST
and t1.HOTEL=#{queryForm.queryHotel,jdbcType=INTEGER}
and t1.ROOM_NO like '${queryForm.queryRoomNo}%'
and t3.NAME like '${queryForm.queryCheckInReceptionistName}%'
and t4.NAME like '${queryForm.queryCheckOutReceptionistName}%'
order by ${queryForm.queryOrderByClause}
limit ${offset}, ${count}

select count(*) from t_room t1 left join ( select a.* from t_check_in a left join (select max(s.ID) as MAX_ID from t_check_in s
and s.CHECK_IN_TIME >= #{queryForm.queryCheckInTimeFrom}
and s.CHECK_IN_TIME <= #{queryForm.queryCheckInTimeFrom}
and s.CHECK_OUT_TIME >= #{queryForm.queryCheckOutTimeFrom}
and s.CHECK_OUT_TIME <= #{queryForm.queryCheckOutTimeFrom}
group by s.ROOM) b on b.MAX_ID=a.ID left join t_device_usage c on c.ROOM=a.ROOM and c.USE_TIME >= a.CHECK_IN_TIME and c.USE_TIME <= ifnull(a.CHECK_OUT_TIME, now()) where b.MAX_ID is not null group by a.ROOM ) t2 on t2.ROOM= t1.ID left join t_user t3 on t3.ID=t2.CHECK_IN_RECEPTIONIST left join t_user t4 on t4.ID=t2.CHECK_OUT_RECEPTIONIST
and t1.HOTEL=#{queryForm.queryHotel,jdbcType=INTEGER}
and t1.ROOM_NO like '${queryForm.queryRoomNo}%'
and t3.NAME like '${queryForm.queryCheckInReceptionistName}%'
and t4.NAME like '${queryForm.queryCheckOutReceptionistName}%'
